const sliderTrigger=document.querySelector('[data-id="slider-open"]'),sliderClose=document.querySelector('[data-id="slider-close"]'),slider1=document.querySelector(".slider-container__inner"),clickAway=document.querySelector(".clickAway");sliderTrigger.addEventListener("click",function(e){e.preventDefault(),slider1.classList.contains("active")?slider1.classList.remove("active"):(slider1.classList.add("active"),clickAway.style.visibility="initial")}),sliderClose.addEventListener("click",function(e){e.preventDefault(),slider1.classList.contains("active")?(slider1.classList.remove("active"),clickAway.style.visibility="hidden"):slider1.classList.add("active")}),clickAway.addEventListener("click",()=>{clickAway.style.visibility="hidden",slider1.classList.remove("active")});